




let solveProblem4 (n:int) (k:int) : int64 =
    let k' = (int64) k
    let result = [3..n] |> Seq.fold (fun state _ -> let (lastMonth,finalMonth) = state
                                                    (finalMonth, (lastMonth * k') + finalMonth))
                                                    (1L, 1L)
    let (last,finalMonth) = result
    finalMonth



[<EntryPoint>]
let main argv = 

    solveProblem1 ""

    solveProblem2 "" = ""

    solveProblem3 "" = ""
    
    solveProblem4 31 5 = 14415648500221L
    
    printfn "%A" argv
    0 // return an integer exit code
